import { Component } from "react";
import Modal from "./component/Modal";

class Image extends Component {
    constructor(props) {
        super(props);
        this.state = {
            contents: [ '/assets/pro03_main.png' ,'/assets/pro03_list.png','/assets/pro03_login.png','/assets/pro03_mobile_main.png','/assets/pro03_mobile_login.png',
            '/assets/pro03_mobile_list.png'],
            index: null,
            hasModal: false,
        };
    }

    controlModal = (index) => {
        this.setState({
            index: index,
            hasModal: !this.state.hasModal
        });
    }

    render() {
        const images = this.state.contents.map((image, index) =>
            <img key={index} onClick={() => this.controlModal(index)} src={image} alt="alt" />
        )

        return (
            <div>
                {images}
                {this.state.hasModal && (
                    <Modal images={this.state.contents} index={this.state.index} close={this.controlModal}></Modal>
                )}
            </div>
        );
    }
}

export default Image;
