import React, { useRef } from 'react';
import './App.css';
import Footer from './component/Footer';
import Header from './component/Header';
import Main from './Main';
import Sayo from './page/Sayo/Sayo';
import Movie from './page/movie/Movie';
import Clone from './page/clone/Clone';
import { Route, Routes, useLocation } from 'react-router-dom';

function App() {
  const contactRef = useRef<HTMLDivElement>(null);
  const location = useLocation();

  const onContactClick = () => {
    contactRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="App">
      <header className="App-header">
        {['/'].includes(location.pathname) && <Header/>}
          <Routes>
            <Route path={process.env.PUBLIC_URL + '/'} element={<Main ref={contactRef}/>}/>
            <Route path={process.env.PUBLIC_URL + '/sayo'} element={<Sayo/>}/>
            <Route path={process.env.PUBLIC_URL + '/movie'} element={<Movie/>}/>
            <Route path={process.env.PUBLIC_URL + '/clone'} element={<Clone/>}/>
          </Routes>
      </header>
      <Footer/>
    </div>
  );
}

export default App;
