import {  useState } from "react";
import Modal from "./component/Modal";

const Image = () => {
    const [state, setState] = useState({
        contents: [ 'assets/pro03_main.png',
            'assets/pro03_list.png',
            'assets/pro03_login.png',
            'assets/pro03_mobile_main.png',
            'assets/pro03_mobile_login.png',
            'assets/pro03_mobile_list.png'],
        index: null,
        hasModal: false,
    });


const controlModal = (index) => {
    setState({
        ...state,
        index: index,
        hasModal: !state.hasModal
    });

}

const images = state.contents.map((image, index) =>
    <img key={index} onClick={() => controlModal(index)} src={image} alt="alt" />
)

return (
    <div>
        {images}
        {state.hasModal && (
            <Modal images={state.contents} index={state.index} close={controlModal} ></Modal>
        )}
    </div>
);

}

export default Image;
